class Solution {
public:

    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<long long>d;
        long long ans=0;
        int n=nums.size();
        for(int i=0;i<n;i++){
            d.push_back(target[i]-nums[i]);
        }
        int left=0;
        while(left<n){
            int m=d[left];
            if(m==0){
                left++;
            }else if(m>0){
                stack<int>st;
                int start=left-1;
                while(left<n&&d[left]>0){
                    bool tag=true;
                    while(!st.empty()&&d[st.top()]>d[left]){
                        if(tag){
                            ans+=d[st.top()]-d[left];
                            tag=false;
                        }
                        st.pop();
                    }
                    st.push(left);
                    left++;
                }
                ans+=d[st.top()];
            }else{
                stack<int>st;
                int start=left-1;
                while(left<n&&d[left]<0){
                    bool tag=true;
                    while(!st.empty()&&d[st.top()]<d[left]){
                        if(tag){
                            ans+=d[left]-d[st.top()];
                            tag=false;
                        }
                        st.pop();
                    }
                    st.push(left);
                    left++;
                }
                ans-=d[st.top()];
            }
        }
        return ans;
    }
