class Solution {
public:
    long long minimumOperations(vector<int>& s, vector<int>& t) {
        int n = s.size(), zc=0;
        vector<long> a(n);
        for(int i=0;i<n;i++){
            a[i] = t[i]-s[i];
            // cout<<a[i]<<" ";
            if(!a[i]) zc++;
        }
        // cout<<endl;
        long long ans=0;
        while(zc < n){
            // cout<<"##"<<" ";
            int idx=0;
            // for(int i=0;i<n;i++)
            //     cout<<a[i]<<" ";
            // cout<<endl;
            while(idx<n){
                // cout<<idx<<" ";
                if(a[idx] > 0){
                    long mn=a[idx], j=idx;
                    while(j+1<n && a[j+1] > 0){
                        mn=min(mn, a[j+1]);
                        j++;
                    }
                    // cout<<idx<<" "<<j<<" "<<mn<<endl;
                    ans += mn;
                    for(int i=idx;i<=j;i++){
                        a[i] -= mn;
                        if(!a[i]) zc++;
                    }
                    idx = j;
                }else if(a[idx] < 0){
                    long mn = abs(a[idx]), j=idx;
                    while(j+1<n && a[j+1] < 0){
                        mn = min(mn, abs(a[j+1]));
                        j++;
                    }
                    // cout<<idx<<" "<<j<<" "<<mn<<endl;
                    ans += mn;
                    for(int i=idx;i<=j;i++){
                        a[i] += mn;
                        if(!a[i]) zc++;
                    }
                    idx = j;
                }
                idx++;
            }

        }
        return ans;
    }
};
