class Solution {
public:
    long long func(vector<int> &v, int n){
        vector<pair<int, int> > w;
        int x = -1;
        int a = v[0], b;
        for(int i=0; i<n; i++){
            if(v[i] >= x){
                x = v[i];
            }
            else{
                b = x;
                w.push_back({a, b});
                a = v[i];
                x = v[i];
            }
        }
        b = v[n-1];
        w.push_back({a, b});
        long long ans=0;
        int m = w.size();
        ans = w[0].second;
        for(int i=1; i<m; i++){
            ans += (w[i].second - w[i].first);
        }
        return ans;
    }

    long long minimumOperations(vector<int>& nums, vector<int>& w) {
        int n = nums.size();
        vector<int> v;
        for(int i=0; i<n; i++){
            v.push_back(w[i] - nums[i]);
        }
        int flag;
        if(v[0] >= 0){
            flag = 1;
        }
        else{
            flag = -1;
        }
        int x;
        long long ans=0;
        vector<int> temp;
        for(int i=0; i<n; i++){
            if(v[i] >= 0) x=1;
            else x = -1;
            while(i<n && x == flag){
                temp.push_back(abs(v[i]));
                i++;
                if(i==n) break;
                if(v[i] >= 0) x=1;
                else x = -1;
            }
            ans += func(temp, temp.size());
            temp.clear();
            i--;
            flag = x;
        }
        return ans;
    }
};
