class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        const int n = nums.size();
        long long ans = 0, prev = 0;
        bool pos = true;
        for (int i = 0; i < n; ++i) {
            int x = target[i] - nums[i];
            if ((x >= 0 && !pos) ||(x < 0 && pos))
                ans += abs(prev), pos = x >= 0;

            if (x >= 0 && x < prev) ans += prev - x;
            else if (x < 0 && x > prev) ans += x - prev;
            prev = x;
        }
        return ans + abs(prev);

    }
};
