class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& t) {
        long long ans=0;
        int n=a.size();
        stack<int> st;
        for (int i=0; i<n; i++) {
            int x=a[i]-t[i];
            if (!x) continue;
            while (i<n && ((x<0 && a[i]-t[i]<0) || (x>0 && a[i]-t[i]>0))) {
                x=a[i]-t[i];
                st.push(abs(x));
                i++;
            }
            x=st.top();
            ans+=x;
            st.pop();
            while (st.size()) {
                if (x < st.top()) {
                    ans+=st.top()-x;
                }
                x=st.top();
                st.pop();
            }
            i--;
        }
        return ans;
    }
};
