class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        a = [x - y for x, y in zip(nums, target)]
        i = 0
        ans = 0
        cnt = 0
        while i < n:
            if i:
                x, y = abs(a[i]), abs(a[i - 1])
                if a[i] * a[i - 1] > 0:
                    if x > y:
                        ans += x - y
                        cnt = y
                else:
                    ans += x
                    cnt = x
            else:
                ans += abs(a[i])
                cnt = abs(a[i])
            i += 1
        return ans
