class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();

        vector<int> vv(n);

        int o=0;
        while(o<n){
            vv[o] = target[o] - nums[o];
            o++;
        }

        long long ans = 0;
        long long cc = 0;




        int i=0;
        while(i<n){
            if (i == 0 || (vv[i - 1] > 0 && vv[i] > 0) || (vv[i - 1] < 0 && vv[i] < 0)) {

                long long tt = abs(vv[i]) - abs(cc);
                if (tt > 0) {
                    ans += tt;
                }

                cc = vv[i];
            } 
            else {

                ans += abs(vv[i]);

                cc = vv[i];
            }

            i++;
        }

        return ans;
    }
};
