class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        min_a = 0
        max_a = 0
        n = len(nums)
        ans = 0
        for i in range(n):
            if target[i] > nums[i]:
                if max_a <= target[i] - nums[i]:
                    ans += target[i] - nums[i] - max_a
                    max_a = target[i] - nums[i]
                    min_a = 0
                else:
                    if max_a - min_a > target[i] - nums[i]:
                        max_a = target[i] - nums[i] + min_a
                    else:
                        min_a = max_a - target[i] + nums[i]
            elif target[i] == nums[i]:
                if max_a > min_a:
                    max_a = min_a
                else:
                    min_a = max_a
            else:
                if min_a <= nums[i] - target[i]:
                    ans += nums[i] - target[i] - min_a
                    min_a = nums[i] - target[i]
                    max_a = 0
                else:
                    if min_a - max_a > -target[i] + nums[i]:
                        min_a = -target[i] + nums[i] + max_a
                    else:
                        max_a = min_a - nums[i] + target[i]
        return ans
