class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long a = 0;
        int l = nums.size();
        for(int i = 0; i < l; ++i) nums[i] -= target[i];
        int t = 0;
        for(auto x: nums){
            if(x == 0){
                ;   
            }else if(x > 0){
                if(t <= 0){
                    a += x;
                }else if(x > t){
                    a += x - t;
                }
            }else{
                if(t >= 0){
                    a -= x;
                }else if(x < t){
                    a -= x - t;
                }
            }
            t = x;
        }
        return a;
    }
};
