class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int len = nums.length;
        long ans = 0;
        long min = 0;
        long[] sub = new long[len];
        for(int i = 0;i < len; i++) {
            sub[i] = nums[i] - target[i];
        }
        int i = 0;
        while(i < len){
            if(sub[i] == 0){
                i++;
            }
            if(i >= len){
                break;
            }
            if(sub[i] > 0) {
                int p = i + 1;
                min = sub[i];
                while(true){
                    if(p >= len){
                        for(int j = i;j < p;j++){
                            sub[j] -= min;
                        }
                        ans += min;
                        break;
                    }
                    if(sub[p] > 0){
                        if(sub[p] < min){
                            min = sub[p];
                        }
                        p++;
                    }else{
                        //统计
                        for(int j = i;j < p;j++){
                            sub[j] -= min;
                        }
                        ans += min;
                        break;
                    }
                }
            }else if(sub[i] < 0){
                int p = i + 1;
                min = sub[i];
                while(true){
                    if(p >= len){
                        //统计
                        for(int j = i;j < p;j++){
                            sub[j] -= min;
                        }
                        ans -= min;
                        break;
                    }
                    if(sub[p] < 0){
                        if(sub[p] > min){
                            min = sub[p];
                        }
                        p++;
                    }else{
                        //统计
                        for(int j = i;j < p;j++){
                            sub[j] -= min;
                        }
                        ans -= min;
                        break;
                    }
                }
            }
        }
        return ans;
    }
}
