class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        int n = nums.size();
        vector<int> diff(n,0);
        for(int i = 0;i<n;i++)
        {
            diff[i] = target[i] - nums[i];
        }
        long long lastop;
        lastop = 0;
        for(int i = 0;i<n;i++)
        {
            if(diff[i] >= 0)
            {
               if(lastop >= diff[i])
               {
                   lastop = 1LL*diff[i];
               }
                else
                {
                    if(lastop >= 0)
                    ans += (1LL*diff[i] - lastop);
                    else
                        ans += (1LL*diff[i]);
                    lastop = 1LL*diff[i];
                }
            }
            else
            {
                if(lastop >= diff[i])
                {
                    if(lastop > 0)
                        ans += abs(1LL*diff[i]);
                    else
                        ans += abs(1LL*diff[i] - lastop);
                    lastop = 1LL*diff[i];
                }
                else
                {
                    lastop = 1LL*diff[i];
                }
            }
        }
        return ans;
    }
};
