class Solution {
public:
int ab(int n){
    return (n>0)?n:(-1*n);
}
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        long long ans=0;
        for(int i=0;i<n;i++){
            nums[i]=target[i]-nums[i];
        }
        int pos=0, length=0,sign=1;
        while(pos<n){
            if(sign*nums[pos]<0){
                length=0;
                sign=sign*(-1);
            }
            if(ab(nums[pos])<length){
                length=ab(nums[pos]);
            }
            else{
                ans+=ab(nums[pos])-length;
                length=ab(nums[pos]);
            }
            pos++;
        }
        return ans;
    }
};
