class Solution {
public:
    long long calculateMinimumCost(vector<int>& values) {
        int localMin = -1;
        long long totalCost = 0;
        int size = values.size();
        for (int i = 0; i < size; i++) {
            if (values[i] <= localMin) {
                if (localMin != -1) localMin = min(localMin, values[i]);
                continue;
            }
            if (localMin == -1) {
                totalCost += values[i];
                localMin = values[i];
                continue;
            }
            totalCost += (values[i] - localMin); 
            localMin = values[i];
        }
        return totalCost;
    }

    long long solve(vector<int>& nums, vector<int>& target) {
        int size = nums.size();
        vector<int> diff;
        int i = 0;
        while (i < size) {
            diff.push_back(nums[i] - target[i]);
            i++;
        }

        long long answer = 0;
        bool isPositive = true;
        vector<int> temp;
        bool lastNonNegative = true;

        i = 0;
        while (i < size) {
            if (diff[i] >= 0) {
                if (!lastNonNegative) {
                    answer += calculateMinimumCost(temp);
                    temp.clear();
                }
                temp.push_back(abs(diff[i]));
                lastNonNegative = true;
            } else {
                if (lastNonNegative) {
                    answer += calculateMinimumCost(temp);
                    temp.clear();
                }
                lastNonNegative = false;
                temp.push_back(abs(diff[i]));
            }
            i++;
        }
        vector<int>v;
        for(int i = 0; i < 1000; i++)v.push_back(i);

        if (!temp.empty()) answer += calculateMinimumCost(temp);
        return answer;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        return solve(nums, target);
    }
};
