def solve(li):
    print("For", li)
    pre = li[0]
    a = None
    b = None
    cost = 0
    for el in li:
        if el >= pre:
            if a == None:
                a = pre
            b = el
        else:
            ## decreasing started
            if b!= None:
                cost += b - a
            a = b = None
        pre = el
    #print(a, b, a==None, b == None)
    if b != None:
        cost += b - a
    return cost

class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        N = len(nums)
        arr = [target[i] - nums[i] for i in range(N)]
        cur = 1
        wind = []
        lis = []
        for el in arr:
            if el == 0:
                if wind:
                    lis.append(wind)
                wind = []
                continue
            else:
                if el * cur > 0:
                    wind.append(el)
                else:
                    if wind:
                        lis.append(wind)
                    wind = [el]
                    cur *= -1

        if wind:
            lis.append(wind)

        #print(lis)
        ans = 0
        for li in lis:
            myLi = [0] + [abs(el) for el in li]
            ans += solve(myLi)

        return ans
