class Solution:
    def minimumOperations(self, a: List[int], b: List[int]) -> int:
        n=len(a)
        c=[i-j for i,j in zip(a,b)]
        dif=[0]*n
        for i in range(n):
            if i==0:
                dif[i]=c[i]
            else:
                dif[i]=c[i]-c[i-1]
        #print(c,dif)
        res=0
        cz=cf=0
        for i in dif:
            if i==0:
                continue
            if i>0:
                if cf>=i:
                    cf-=i
                else:
                    i-=cf
                    cf=0
                    cz+=i
                    res+=i
            if i<0:
                i=abs(i)
                if cz>=i:
                    cz-=i
                else:
                    i-=cz
                    cz=0
                    cf+=i
                    res+=i
            #print(cz,cf,i,res)
        return res
