class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long res = 0;
        for (int i = 0; i < nums.size(); i++) {
            nums[i] -= target[i];
        }

        long long temp = nums[0];
        for (int i = 1; i < nums.size(); i++) {
            if (nums[i] == 0 || (temp < 0) != (nums[i] < 0)) {
                res += abs(temp);
            }
            else if (abs(temp) > abs(nums[i])) {
                res += (abs(temp) - abs(nums[i]));
            }
            temp = nums[i];
        }
        res += abs(temp);
        return res;
    }
};
