class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
          long long op=0;
          int n=target.size();
          int prev=0;
          int indc=0;
          for(int i=0;i<n;i++){
              if(nums[i]==target[i]){
                  prev=0;
                  indc=0;
              }
              if(nums[i]<target[i]){
                   if(prev==1){
                       int k=target[i]-nums[i];
                       nums[i]+=min(k,indc);
                       op+=(target[i]-nums[i]);
                       indc=k;
                   }
                   else {
                     indc=(target[i]-nums[i]);
                     op+=(target[i]-nums[i]);
                       prev=1;
                   }
              }
              else{
                  if(prev==0){
                      int k=nums[i]-target[i];
                      nums[i]-=min(k,indc);
                      op+=(nums[i]-target[i]);
                      indc=k;

                  }
                  else {
                     indc=(nums[i]-target[i]);
                    op+=(nums[i]-target[i]);
                    prev=0;
                  }
              }
          }
          return op; 
    }
};
