class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long result = 0;
        long long operationstaken = 0;
        int n = nums.size();
        int c = 0;
        vector<int> differencearray(n);
        for(int i=0;i<n;i++){
            differencearray[i]=target[i]-nums[i];
        }
        operationstaken = differencearray[0];
        result = abs(operationstaken);

        for(int i=1;i<n;i++){
            if(differencearray[i]>=differencearray[i-1]){
                operationstaken = differencearray[i];
                if(differencearray[i]>0){

                    result+=min(differencearray[i] , differencearray[i]-differencearray[i-1]);
                }

            }
            else{
                operationstaken = differencearray[i];
                if(differencearray[i]<0){
                    result+=min(abs(differencearray[i]) , abs(differencearray[i-1]-differencearray[i]));
                }
            }


        }
        return result;
    }
};
