#define ll long long int
class Solution {
public:
    ll solve(vector<ll>& t) {
        stack<ll>st;
        ll ans=t[0];
        st.push(t[0]);
        for(int i=1;i<t.size();i++){
            while(!st.empty() && st.top()>=t[i]) st.pop();
            if(!st.empty()){
                ans+=t[i]-st.top();
                st.pop();
            }
            st.push(t[i]);
        }
        return ans;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<ll>v;
        for(int i=0;i<nums.size();i++)
        {
            v.push_back(target[i]-nums[i]);
        }
        ll ans=0;
        int i=0;
        while(i<v.size())
        {
            if(v[i]>0)
            {
                vector<ll>v1;
                while(i<v.size() && v[i]>0)
                {
                    v1.push_back(v[i]);
                    i++;
                }
                ans+=solve(v1);
            }
            else if(v[i]<0)
            {
                vector<ll>v1;
                while(i<v.size() && v[i]<0)
                {
                    v1.push_back(abs(v[i]));
                    i++;
                }
                ans+=solve(v1);
            }
            else
            {
                i++;
            }
        }
        return ans;
    }
};
