class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        lis = []
        for i,j in zip(nums, target):
            lis.append(i-j)
        # print(lis)
        sm = 0
        res = 0
        for i in lis:
            if i > 0 and sm >= 0:
                res += max(i-sm, 0)
            elif i > 0 and sm <= 0:
                res += i
            elif i < 0 and sm >= 0:
                res += -i
            elif i < 0 and sm <= 0:
                res += max(sm-i, 0)

            sm = i
        return res
