class Solution {
public:

    //Take negs and turn into 0s
    long long calculateMinOpsNeg(vector<int>&nums){

        int res = abs(nums[0]);

        for(int i = 1; i<nums.size(); ++i){
            if(nums[i] < nums[i-1]){
                res+=abs((nums[i] + abs(nums[i-1])));
            }
        }
        return res;
    }

    //Take pos and turn into 0s
    long long calculateMinOpsPos(vector<int>&nums){
        int res = nums[0];

        for(int i = 1; i<nums.size(); ++i){
            if(nums[i] > nums[i-1]){
                res+=(nums[i] - nums[i-1]);
            }
        }
        return res;
    }

    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        /*for(int i = 0; i<target.size(); ++i){
            nums[i] = nums[i] - target[i];
        }*/

        for(int i = 0; i<target.size(); ++i){
            target[i] = target[i] - nums[i];
        }
        /*cout<<"difference vector : ";
        for(auto num : target){
            cout<<num<<" "; 
        }cout<<endl; */

        vector<int>current_segment; 
        bool is_pos = true;
        long long ans = 0; 

        for(int i = 0; i<target.size(); ++i){
            int num = target[i]; 

            if(num > 0 && is_pos == false){
                if(current_segment.empty()){
                    current_segment.push_back(num);
                    is_pos = true; 
                }else{
                    /*cout<<"calling calculateMinOpsNeg"<<endl; 
                    cout<<"current segmemt : "; 
                    for(auto num: current_segment){
                        cout<<num<<" "; 
                    }cout<<endl; */
                    ans+=calculateMinOpsNeg(current_segment); 
                    /*cout<<"new ans : "<<ans<<endl; 
                    cout<<" "<<endl; */
                    current_segment.clear(); 
                    current_segment.push_back(num);
                    is_pos = true; 
                }

            }else if(num < 0 && is_pos == true){
                if(current_segment.empty()){
                    current_segment.push_back(num);
                    is_pos = false; 
                }else{
                    /*cout<<"calling calculateMinOpsPos"<<endl; 
                    cout<<"current segmemt : "; 
                    for(auto num: current_segment){
                        cout<<num<<" "; 
                    }cout<<endl; */
                    ans+=calculateMinOpsPos(current_segment); 
                    //cout<<"new ans : "<<ans<<endl; 
                    //cout<<" "<<endl; 
                    current_segment.clear(); 
                    current_segment.push_back(num);
                    is_pos = false; 
                }
            }else{
                current_segment.push_back(num);
            }
        }

        //clean up 
        if(!current_segment.empty()){
            if(is_pos){
                /*cout<<"calling calculateMinOpsNeg"<<endl; 
                cout<<"current segmemt : "; 
                    for(auto num: current_segment){
                        cout<<num<<" "; 
                    }cout<<endl; */
                ans+=calculateMinOpsPos(current_segment);
                //cout<<"new ans : "<<ans<<endl; 
            }else{
                /*cout<<"calling calculateMinOpsPos"<<endl; 
                cout<<"current segmemt : "; 
                    for(auto num: current_segment){
                        cout<<num<<" "; 
                    }cout<<endl; */
                ans+=calculateMinOpsNeg(current_segment); 
                //cout<<"new ans : "<<ans<<endl; 
            }
        }

        return ans; 
    }
};
