#include <vector>
#include <cmath>
#include <algorithm>
using namespace std;

class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> sub(n);

        for (int i = 0; i < n; ++i) {
            sub[i] = target[i] - nums[i];
        }

        long long total = 0;
        int currSub = 0;

        for (int i = 0; i < n; ++i) {

            if (i == 0 || (sub[i - 1] > 0 && sub[i] > 0) || (sub[i - 1] < 0 && sub[i] < 0)) {
                long long op = abs(sub[i]) - abs(currSub);
                if (op > 0) {
                    total += op;
                }
                currSub = sub[i];
            } 

            else {
                total += abs(sub[i]);
                currSub = sub[i];
            }
        }

        return total;
    }
};
