class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long res=0;
        long long a=0,b=0;
        int n=nums.size();
        std::vector<long long> u(n+1);
        for(int i=1;i<=n;i++){
            u[i]=target[i-1]-nums[i-1];
        }
        std::vector<long long> term(n+1);
        for(int i=1;i<=n;i++){
            term[i]=u[i]-u[i-1];
            std::cout<<term[i]<<' ';
            if(term[i]>0){
                a+=term[i];
            }
            else{
                b-=term[i];
            }
        }
        std::cout<<a<<' '<<b;
        res=max(a,b);
        return res;
    }
};
