class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        long data[] = new long[target.length];
        for(int i=0;i<target.length;i++) data[i] = target[i]-nums[i];
        long output = 0l;
        for(int i=0;i<data.length;i++){
            int j = i;
            boolean check = data[i]>0;
            if(check) while(i+1<data.length && data[i+1]>0) i++;
            else while(i+1<data.length && data[i+1]<0) i++;
            //System.out.println(j+"   "+i+"   "+output);
            output += Math.abs(data[j]);
            if(check) for(j=j+1;j<=i;j++) output += Math.max(data[j]-data[j-1],0);
            else for(j=j+1;j<=i;j++) output += Math.abs(Math.min(data[j]-data[j-1],0));
            //System.out.println(j+"   "+i+"   "+output);
        }
        return output;
    }
}
