class Solution {
public:
    bool check(long long op,vector<int> &nums,int l,int r)
    {
           long long cnt=0;
           int t=0;
           for(int i=l;i<=r;i++)
           {
               if(t>=nums[i])
               {
                   t=nums[i];
               }
               else
               {
                   cnt+=nums[i]-t;
                   t=nums[i];
               }

           }
           if(cnt<=op)
           return true;

           return false;
    }
    long long fun(int l,int r,vector<int> &nums)
    {
         for(int i=l;i<=r;i++)
         {
            nums[i]=abs(nums[i]);
         }

         long long low=0;
         long long high=1e15;
         long long ans=0;
         while(low<=high)
         {
              long long mid=(low+high)/2;
              if(check(mid,nums,l,r)==true)
              {
                  ans=mid;
                  high=mid-1;
              }
              else low=mid+1;
         }
         return ans;


    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans=0;
        int n=nums.size();
        vector<int> v(n);
        for(int i=0;i<n;i++)
        {
            v[i]=nums[i]-target[i];
        }
        // for(auto it:v)
        // cout<<it<<" ";
        // cout<<endl;
        for(int i=0;i<n;)
        {
             int prev=v[i];
             int j=i;
             while(j<n)
             {
                 if(prev>=0&&v[j]>=0){
                 prev=v[j];
                 j++;
                 }

                 else if(prev<=0&&v[j]<=0)
                 {
                    prev=v[j];
                    j++;
                 }

                 else break;
             }
             long long x=fun(i,j-1,v);
            // cout<<i+1<<" "<<j<<" "<<x<<endl;
             ans+=x;
             i=j;
        }
        return ans;
    }
};
