class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int n = nums.length;
        int[] diff = new int[n];
        for(int i = 0; i < n; i++)
            diff[i] = target[i] - nums[i];
        int[] d = new int[n];
        d[0] = diff[0];
        for(int i = 1; i < n; i++)
            d[i] = diff[i] - diff[i-1];
        long ans = 0;
        long psum = 0;
        long nsum = 0;
        for(int i = 0; i < n; i++){
            if(d[i] > 0)
                psum += d[i];
            else
                nsum -= d[i];
        }
        return Math.max(psum, nsum);
    }
}
