class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> diff(n, 0);

        for (int i = 0; i < n; ++i)diff[i] = target[i] - nums[i];

        long long ans=0;
        long long curr=0;

        for (int i = 0; i < n; ++i) {
            if (i==0 || (diff[i-1]>0 && diff[i]>0) || (diff[i-1]<0 && diff[i]<0)) {
                long long ext = abs(diff[i])-abs(curr);
                if (ext>0)ans+=ext;
                curr=diff[i];
            } else {
                ans+=abs(diff[i]);
                curr=diff[i];
            }
        }

        return ans;
    }
};
