class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int n = nums.length;
        long result = 0;
        int curr = 0;

        while (curr < n) {
            int i = curr;
            int min = target[i] - nums[i];

            if (min > 0) {
                int j = i;

                // System.out.println("ji");
                int minValue = Integer.MAX_VALUE;
                while (j < n && (target[j] - nums[j]) > 0) {
                    minValue = Math.min(minValue, target[j] - nums[j]);
                    j++;
                }
                for (int k = i; k < j; k++) {
                    nums[k] += minValue;
                }
                result+=minValue;
            } else if (min < 0) {
                // System.out.println("ok");
                int j = i;
                int minValue = Integer.MAX_VALUE;
                while (j < n && (target[j] - nums[j]) < 0) {
                    minValue = Math.min(minValue, Math.abs(target[j] - nums[j]));
                    j++;
                }
                for (int k = i; k < j; k++) {
                    nums[k] -= minValue;
                }
                result+=minValue;
            } else {
                curr++;
            }
        }
        // for(int l=0;l<n;l++){
        //     System.out.println(nums[l]);
        // }
        return result;
    }
