class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        vector<long long> diff(n);
        for(int i=0;i<n;i++){
            int x= target[i]-nums[i];
            diff[i]=x;
        }
        long long mn=0,ans=0;
        for(int i=0;i<n;i++){
            if(diff[i]>=0){
                if(diff[i]-mn>=0){
                    ans+=diff[i]-mn;
                }
                mn=diff[i];
            }
            else{
                mn=0;
            }
        }
        mn=0;
        for(int i=0;i<n;i++){
            if(diff[i]<0){
                if(diff[i]-mn<=0){
                    ans+=abs(diff[i]-mn);
                }
                mn=diff[i];
            }
            else{
                mn=0;
            }
        }
        return ans;
    }
};
