using ll = long long;

class Solution {
public:
    ll min_ops(vector<int>& nums, vector<int>& target) {
        int n = nums.size();

        ll cnt = 0;
        int min_diff = 1e9;
        int r = 0;
        while (r < n && nums[r] == target[r]) r++;
        if (r == n) return 0;
        int l = r;
        bool to_inc = nums[r] < target[r];

        while (r < n) {
            if (to_inc && nums[r] < target[r]) {
                min_diff = min(min_diff, target[r] - nums[r]);
                r++;
            } else if (!to_inc && nums[r] > target[r]) {
                min_diff = min(min_diff, nums[r] - target[r]);
                r++;
            } else {
                cnt += min_diff;
                for (int i = l; i < r; i++) {
                    nums[i] += (to_inc ? min_diff : -min_diff);
                }
                min_diff = 1e9; 

                while (r < n && nums[r] == target[r]) r++;
                if (r == n) break;
                to_inc = nums[r] < target[r];
                l = r;
            }
        }

        return cnt;
    }

    ll minimumOperations(vector<int>& nums, vector<int>& target) {
        nums.push_back(0);
        target.push_back(0);

        ll cnt = min_ops(nums, target);
        while (nums != target) {
            cnt += min_ops(nums, target);
        }
        return cnt;
    }
};
