class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int len = nums.length;
        long[] diffn = new long[len+1];
        long[] difft = new long[len+1];
        diffn[0] = nums[0];
        difft[0] = target[0];
        for (int i = 1; i < len; i++) {
            diffn[i] = nums[i] - nums[i-1];
            difft[i] = target[i] - target[i-1];
        }
        diffn[len] = -nums[len-1];
        difft[len] = -target[len-1];

        long ans = 0;
        for (int i = 0; i <= len; i++) {
            if (diffn[i] > difft[i]) {
                ans += diffn[i] - difft[i];
            }
        }
        return ans;
    }
}
