#define ll long long
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<ll> diff(n);
        for (int i=0;i<n;i++) {
            diff[i] = (ll)(target[i]) - nums[i];
        }
        ll ans = 0;
        ll currDiff = 0;
        for (int i=0;i<n;i++) {
            ll temp = diff[i];
            if (i == 0 || (currDiff > 0 && temp > 0) || (currDiff < 0 && temp < 0)) {
                ll req = abs(temp) - abs(currDiff);
                if (req > 0) {
                    ans += req;
                }
                currDiff = temp;
            } 
            else {
                ans += abs(temp);
                currDiff = temp;
            }
        }
        return ans;
    }
};
