class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int>diff;
        int n = nums.size();
        long long ans=0;
        for(int i=0;i<n;i++){diff.push_back(target[i]-nums[i]);}
        // for(int i=0;i<n;i++){
        //     cout<<diff[i]<<" ";}
        for(int i=0;i<n;i++){
            // cout<<diff[i]<<" ";
            if(diff[i]==0){continue;}
            else if(diff[i]>0){
                int mac=diff[i],j=i,k=i;
                while(i<n&&diff[i]>0){mac=min(diff[i],mac);i++;}
                i--;ans+=mac;
                for(;j<=i;j++){
                    diff[j]=diff[j]-mac;
                }
                i=k-1;
            }
            else{
                int mac=-diff[i],j=i,k=i;
                while(i<n&&diff[i]<0){mac=min(-diff[i],mac);i++;}
                i--;ans+=mac;
                for(;j<=i;j++){
                    diff[j]=diff[j]+mac;
                }
                i=k-1;
            }
        }
        return ans;
    }
};
