class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n= size(nums);
        for(int i=0;i<n;i++){
            nums[i]=target[i]-nums[i];
        }
        long long ans=0;
        for(int i=0;i<n;){
            if(nums[i]==0){
                i++;
                continue;
            }
            if(nums[i]>0){
                int oreq=nums[i];
                ans+=oreq;
                i++;
                while(i<n && nums[i]>0){
                    if(nums[i]>oreq){
                        ans+=(nums[i]-oreq);
                    }
                        oreq=nums[i++];
                }
            }else{
                int oreq=(nums[i]*-1);
                ans+=oreq;
                i++;
                while(i<n && nums[i]<0){
                    if((nums[i]*-1)>oreq){
                        ans+=((nums[i]*-1)-oreq);
                    }
                        oreq=(nums[i]*-1);
                        i++;
                }
            }
        }
        return ans;
  }
};
