class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        dif = [target[i] - nums[i] for i in range(n)]

        pre = [0] * (n + 1)
        pre[0] = dif[0]
        x1 = x2 = 0
        for i in range(1, n):
            pre[i] = dif[i] - dif[i - 1]
        for i in range(n):
            if pre[i] > 0:
                x1 += pre[i]
            elif pre[i] < 0:
                x2 -= pre[i]

        return max(x1,x2)
