class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        for (int i=0; i<n; i++) {
            nums[i]-=target[i];
            cout<<nums[i]<<" ";
        }
        long long ans=0;
        vector<int> stneg, stpos;
        for (int i=0; i<n; i++) {
            if (nums[i]<0) {
                // stpos pop
                stneg.push_back(nums[i]);
                if (stpos.empty()) continue;
                long long cur=stpos[0];
                for (int j=1; j<stpos.size(); j++) {
                    cur+=max(stpos[j]-stpos[j-1],0);
                }
                ans+=cur;
                stpos.clear();
            } else if (nums[i]>=0) {
                // stneg pop
                stpos.push_back(nums[i]);
                if (stneg.empty()) continue;
                long long cur=abs(stneg[0]);
                for (int j=1; j<stneg.size(); j++) {
                    cur+=max(abs(stneg[j])-abs(stneg[j-1]),0);
                }
                ans+=cur;
                stneg.clear();
            }
        }
        if (stneg.size()) {
            long long cur=abs(stneg[0]);
            for (int j=1; j<stneg.size(); j++) {
                cur+=max(abs(stneg[j])-abs(stneg[j-1]),0);
            }
            ans+=cur;
        }
        if (stpos.size()) {
            long long cur=stpos[0];
            for (int j=1; j<stpos.size(); j++) {
                cur+=max(stpos[j]-stpos[j-1],0);
            }
            ans+=cur;
        }
        return ans;
    }
};
