class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long res=0;
        int n= nums.size();
        for(int i=0; i<n; i++) nums[i]=target[i]-nums[i];
        int prev=0;
        nums.push_back(0);
        for(int i=0; i<n; i++) {
            // cout<<nums[i]<<" ";
            if(nums[i]>0){
                if(nums[i]>nums[i+1]){
                    res+=min(nums[i]-nums[i+1], nums[i]);
                }
            }
            nums[i]*=-1;
        }
        // cout<<'\n';
        for(int i=0; i<n; i++) {
            // cout<<nums[i]<<" ";
            if(nums[i]>0){
                if(nums[i]>nums[i+1]){
                    res+=min(nums[i]-nums[i+1], nums[i]);
                }
            }
        }
        return res;

    }
};
