class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size(), curr=0;
        long long change =0;
        vector<int>diffs;
        for(int i=0; i<n; i++)diffs.push_back(target[i]-nums[i]);
        for(int i=0; i<n; i++){
            if(curr!=0 && diffs[i]*(curr/abs(curr))<0)curr=0;
            int temp = abs(diffs[i])-abs(curr);
            change+= (temp>0? temp:0);
            curr = diffs[i];
        }
        return change;
    }
};
