class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        d=[t-n for t,n in zip(target, nums)]
        ops, lc = 0, 0
        for diff in d:
            if diff==0:
                lc=diff
                continue
            if (lc>=0 and diff>=0) or (lc<=0 and diff<=0):
                if abs(diff)>abs(lc): ops+=abs(diff)-abs(lc)
            else: ops+=abs(diff)
            lc=diff
        return ops
