class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long res=0;
        int n=nums.size();
        vector<long long> diff;
        for(int i=0;i<n;i++){
            diff.push_back((long long)target[i]-nums[i]);
        }
        diff.push_back(0);
        stack<long long> os;

        for(int i=0;i<n+1;i++){

            if(os.empty()){
                os.push(0);
                os.push(diff[i]);
                continue;
            }
            if(diff[i]>0 && os.top()>0){
                long long cur=os.top();
                while(!os.empty() && os.top()>diff[i]){
                    res+=(cur-os.top());
                    cur=os.top();
                    os.pop();
                }
                if(cur>diff[i]){
                     res+=(cur-diff[i]);
                }
            }else if(diff[i]<=0 && os.top()>0){
                long long cur=os.top();
                while(!os.empty()){
                    res+=(cur-os.top());
                    cur=os.top();
                    os.pop();
                }
            }else if(diff[i]>=0 && os.top()<0){
                long long cur=os.top();
                while(!os.empty()){
                    res+=(os.top()-cur);
                    cur=os.top();
                    os.pop();
                }
            }else if(diff[i]<0 && os.top()<0){
                long long cur=os.top();
                while(!os.empty() && os.top()<diff[i]){
                    res+=(os.top()-cur);
                    cur=os.top();
                    os.pop();
                }
                if(cur<diff[i]){
                     res+=(diff[i]-cur);
                }
            }


            if(os.empty()){
                os.push(0);
            }
            os.push(diff[i]);
        }


        return res;
    }
};
