class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        for(int i=0; i<n; i++){
            target[i] = target[i] - nums[i];
        }
        int i=0;
        long long ans = 0;
        long long temp = 0;
        while(i<n){
            while(i<n and target[i] == 0) i++;
            if(i == n) break;
            if(target[i] >= 0){
                temp = target[i++];
                while(i<n and target[i] > 0){
                    if(target[i] >= target[i-1]){
                        temp = target[i++];
                    } else {
                        ans += temp;
                        temp = target[i++];
                        ans -= temp;
                    }
                }
                ans += temp;
            } else {
                temp = abs(target[i++]);
                while(i<n and target[i] < 0){
                    if(target[i] <= target[i-1]){
                        temp = abs(target[i++]);
                    } else {
                        ans += temp;
                        temp = abs(target[i++]);
                        ans -= temp;
                    }
                }
                ans += temp;
            }
        }
        return ans;
    }
};
