class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans=0;
        vector<int>diff;
        for(int i=0;i<nums.size();i++)
        {
              diff.push_back(target[i]-nums[i]);
        }
        int pos=0;
        int neg=0;
        for(int i=0;i<diff.size();i++)
        {
            if(diff[i]>0)
            {   neg=0;
                if(diff[i]>pos)
                {
                      ans+=diff[i]-pos;
                    pos=diff[i];
                }
                else
                    pos=diff[i];

            }
            else if(diff[i]<0)
            {    pos=0;

                    if(abs(diff[i])>neg)
                    {
                          ans+=abs(diff[i])-neg;
                        neg=abs(diff[i]);
                    }
                  else
                      neg=abs(diff[i]);
            }
            else
            {
                pos=0;
                neg=0;
            }
        }
        return ans;
    }
};
