class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        total=0
        for i,num in enumerate(nums):
            if num!=target[i]:
                dif = target[i]-num
                total+=abs(dif)
                sign=1
                if dif<0:
                    sign=-1

                deviate=0
                while dif!=0 and deviate+i<len(target):
                    if (target[deviate+i]-nums[deviate+i])*sign < dif*sign:
                        if (target[deviate+i]-nums[deviate+i])*sign>0:
                            dif=target[deviate+i]-nums[deviate+i]
                            nums[deviate+i]+=dif
                        else:
                            dif=0
                    else:
                        nums[deviate+i]+=dif
                    deviate+=1

        return total
