class Solution {
public:
    long long calc(int start, int end, vector<long long>& arr) {
        for(int i=start;i<end;i++) {
            arr[i] = abs(arr[i]);
        }
        long long ret = arr[start];
        for (int i=(start+1);i<end;i++) {
            long long v = max(arr[i] - arr[i - 1], 0LL);
            ret += v;
        }

        return ret;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<long long> diff(n);
        for(int i=0;i<n;i++) {
            diff[i]=nums[i]-target[i];
        }

        long long ret = 0;
        for(int i=0;i<n;) {
            if(diff[i] == 0) {
                i++;
                continue;
            }
            int start = i;
            if(diff[i] < 0) {
                while(i<n && diff[i] < 0) {
                    i++;
                }
                ret += calc(start, i, diff);
            } else {
                while(i<n && diff[i] > 0) {
                    i++;
                }
                ret += calc(start, i, diff);
            }
        }

        return ret;


    }
};
