class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:

        for i in range(len(nums)):
            nums[i] = target[i] - nums[i]
        step, prev = 0, 0
        print(nums)
        for num in nums:
            if prev >= 0:
                if num > prev:
                    step += num - prev
                elif num <= 0:
                    step += abs(num)
                prev = num
            elif prev < 0:
                if num < prev:
                    step += abs(num - prev)
                elif num >= 0:
                    step += num
                prev = num
        return step
