class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int l=0;
        int h=0;
        boolean plus=true;
        long ans=0;
        while(!Arrays.equals(nums,target) && l!=nums.length){
            if(nums[l]==target[l]){
                l++;
                continue;
            }
            plus=true;
            if(nums[l]>target[l])plus=false;
            if((plus)){
                h=l;
                int minDiff=Math.abs(nums[h]-target[h]);
                while(h+1<nums.length && nums[h+1]<target[h+1]){
                    h++;
                    minDiff=Math.min(minDiff,Math.abs(nums[h]-target[h]));
                }
                for(int i=l;i<=h;i++){
                    nums[i]+=minDiff;
                }
                ans+=minDiff;
            }
            else{
                 h=l;
                int minDiff=Math.abs(nums[h]-target[h]);
                while(h+1<nums.length && nums[h+1]>target[h+1]){
                    h++;
                    minDiff=Math.min(minDiff,Math.abs(nums[h]-target[h]));
                }
                for(int i=l;i<=h;i++){
                    nums[i]-=minDiff;
                }
                ans+=minDiff;
            } 
        }
        return ans;
    }
}
