class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans=0;

        vector<int> diff;
        for(int i=0;i<nums.size();i++) diff.push_back(target[i]-nums[i]);
          int prev=diff[0];
        ans+=abs(prev);
        for(auto it: diff){
            if(it!=prev){
                if(prev>0 && it>0 && it>prev){
                    ans+= (it-prev);
                }
                if(prev<0 && it>0){
                    ans+=it;
                }
                if(prev<0 && it<0 && it<prev){
                    ans+= (abs(it)-abs(prev));
                }
                if(it<0 && prev>0){
                    ans+=abs(it);
                }
                if(prev==0 && it>0){
                    ans+=it;
                }
                if(prev==0 && it<0){
                    ans+=abs(it);
                }
            }
            prev=it;
        }
        return ans;
    }
    // 3+1+
};
