class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        long ans = 0L, prev = 0L;
        for (int i = 0; i < nums.length; i++){
            int req = target[i] - nums[i];
            if (req == 0) {
                prev = 0;
                continue;
            }
            if (req > 0){
                if (prev < 0) prev = 0;
                if (req >= prev){
                    ans += (req-prev);
                    prev = req;
                }
                else prev = req;
            }
            else{
                if (prev > 0) prev = 0;
                if (Math.abs(req) >= Math.abs(prev)){
                    ans += (Math.abs(req) - Math.abs(prev));
                    prev = req;
                }
                else prev = req;
            }
        }
        return ans;
    }
}
