class Solution {
public:
    using ll = long long int;
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        int i = 0;
        ll ans = 0;
        vector<int> diff;
        for(int i = 0; i < n; i ++) {
            diff.push_back(target[i] - nums[i]);
        }
        while(i < n) {
            if(diff[i] == 0) {
                i ++;
                continue;
            }
            if(diff[i] > 0) {
                int j = i + 1;
                ans += diff[i];
                while(j < n and diff[j] > 0) {
                    ans += max(diff[j] - diff[j - 1], 0);
                    j ++;
                }
                i = j;
            }else {
                int j = i + 1;
                ans += abs(diff[i]);
                while(j < n and diff[j] < 0) {
                    ans += abs(min(diff[j] - diff[j - 1], 0));
                    j ++;
                }
                i = j;
            }
        }
        return ans;
    }
};

