class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        ans = 0
        cd = 0       
        ds = [target[i] - nums[i] for i in range(len(nums))]       
        for i in range(len(ds)):
            if i == 0 or (ds[i - 1] > 0 and ds[i] > 0) or (ds[i - 1] < 0 and ds[i] < 0):
                aos = abs(ds[i]) - abs(cd)
                cd = ds[i]
                if aos > 0:
                    ans += aos
            else:
                cd = ds[i]  
                ans += abs(ds[i])
        return ans
